import './styles/App.css';
import logo from './img/taipei-city-logo.png'
import {
  devUrl
} from './config'
import axios from 'axios'
import chartjs from 'chart.js'
function App() {
    const ctx = document.getElementById("example"),
      example = new Chart(ctx, {
        // 參數設定[註1]
        type: "bar", // 圖表類型
        data: {
          labels: ["共同生活戶", "單獨生活戶"], // 標題
          datasets: [{
              label: '女性', // 標籤
              data: [20, 19], // 資料
              backgroundColor: [ // 背景色
                "#DF7B7B"
              ],
              borderWidth: 0.5 // 外框寬度
            },
            {
              label: '男性', // 標籤
              data: [12, 19], // 資料
              backgroundColor: [ // 背景色
                "#478CD1"
              ],
              borderWidth: 0.5 // 外框寬度

            }
          ]
        }
      });

  return (
    <>
      <div className="container">
   <div className="tpgov_logo mb-3 mt-4 mx-auto">
   <img src={logo} alt=""></img>
  </div> 
  <p className="title mb-5">108年台北市各區戶數資料</p>
  <div className="d-flex align-center mb-3">
    <span className="district ml-4">
      行政區
    </span> 
    <div>
      <select name="" id="tpedist" >
        <option>請選擇</option>

        <option value=""></option>

      </select>
    </div>
  </div>
 <div className="row">
   <canvas id="example" className="col-10 mb-3" ></canvas>
 </div>
</div>
    </>
  );
}

export default App;
