import React, {
  useEffect,
  useState

} from 'react'
import './styles/App.css';
import logo from './img/taipei-city-logo.png'
import BarChart from './component/BarChart'
import {
  API_HOST
} from './config'
import axios from 'axios'

function App() {
  const [districtOpt, setDistrictOpt] = useState([{}])

  useEffect(() => {
    (async () => {
      let rawdata = await axios.get(`${API_HOST}`)
      let r = rawdata.filter((v) => {
        return v.site_id.slice(0, 3) === "臺北市";
      })
      let set = new Set();
      const result = r.filter(v => {
        return !set.has(v.site_id) ? set.add(v.site_id) : false
      })
      setDistrictOpt(result)

    })()
  }, [])

  return ( <
    >
    <
    div className = "container" >
    <
    div className = "tpgov_logo mb-3 mt-4 mx-auto" >
    <
    img src = {
      logo
    }
    alt = "" > < /img> <
    /div>  <
    p className = "title mb-5" > 108 年台北市各區戶數資料 < /p> <
    div className = "d-flex align-center mb-3" >
    <
    span className = "district ml-4" >
    行政區 <
    /span>  <
    div >
    <
    select name = ""
    id = "tpedist" >
    <
    option > 請選擇 < /option> {
      districtOpt.length !== 0 ? (districtOpt.map((district, key) => {
          return ( <
            option value = "" > {
              district.site_id.slice(3)
            } < /option>

          )
        })) : ( < > < />)

        }

        <
        /select> <
        /div> <
        /div> <
        div className = "row" >
        <
        BarChart / >
        <
        /div> <
        /div> <
        />
    );
  }

  export default App;
