import React from 'react'

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';


ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,

};

const labels = ["共同生活戶", "單獨生活戶"];


function BarChart(props) {
  const {
    dataSet
  } = props
  return (
    <>
        <Bar options = {
          options
        }
        data = {
          {
            labels,
            dataSet
          }
        }
        />
    </>
  )
}

export default BarChart
